// Helper to get all unique tags from color themes
export function getAllThemeTags(): string[] {
  const tags = new Set<string>();
  colorThemes.forEach(theme => {
    theme.tags?.forEach(tag => tags.add(tag));
  });
  return Array.from(tags).sort();
}
